//SPDX-License-Identifier: MIT

// GET LISTED ON OPENSEA: https://testnets.opensea.io/get-listed/step-two

// Defining Library

library SockLayoutLibrary {
    function GetColor(uint256 colorInt) public pure returns(string memory){ 
       string memory color ; 
       if(colorInt == 0 ) {
            // RED
           color = "#EB4545";
       }
       else if (colorInt == 1) {
            // Pink
            color = "#FDA2F4";
       }
       else if(colorInt == 2) {
          //Orange
          color ="#FF8A01";
       } 
       else if(colorInt == 3) {
         //Yellow
         color = "#FDED5E";
       }
       else if(colorInt == 4) {
         //Green
         color = "#B4E847";
       }
       else if(colorInt == 5) {
         //Dark Green
         color = "#70AE3F";
